files.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
}

@layer base {
  :root {
    /* NAVAX Brand: Magenta #79217a + Teal #006F6E */
    --background: 0 0% 100%;
    --foreground: 210 11% 15%;
    --card: 0 0% 100%;
    --card-foreground: 210 11% 15%;
    --popover: 0 0% 100%;
    --popover-foreground: 210 11% 15%;

    /* Primary: NAVAX magenta */
    --primary: 299 57% 30%;
    --primary-foreground: 0 0% 100%;

    /* Secondary: NAVAX teal */
    --secondary: 180 100% 22%;
    --secondary-foreground: 0 0% 100%;

    --muted: 210 14% 96%;
    --muted-foreground: 210 8% 46%;

    --accent: 180 30% 94%;
    --accent-foreground: 180 100% 18%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    --success: 160 65% 38%;
    --success-foreground: 0 0% 100%;

    --warning: 38 92% 50%;
    --warning-foreground: 0 0% 100%;

    --info: 212 63% 43%;
    --info-foreground: 0 0% 100%;

    --border: 210 14% 89%;
    --input: 210 14% 89%;
    --ring: 299 57% 30%;

    --chart-1: 299 57% 30%;
    --chart-2: 180 100% 22%;
    --chart-3: 212 63% 43%;
    --chart-4: 38 92% 50%;
    --chart-5: 160 65% 38%;

    --radius: 0.5rem;

    --sidebar-background: 210 20% 98%;
    --sidebar-foreground: 210 11% 25%;
    --sidebar-primary: 299 57% 30%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 180 30% 94%;
    --sidebar-accent-foreground: 180 100% 18%;
    --sidebar-border: 210 14% 89%;
    --sidebar-ring: 299 57% 30%;
  }

  .dark {
    --background: 210 15% 7%;
    --foreground: 210 14% 95%;
    --card: 210 15% 9%;
    --card-foreground: 210 14% 95%;
    --popover: 210 15% 9%;
    --popover-foreground: 210 14% 95%;

    --primary: 299 50% 58%;
    --primary-foreground: 0 0% 100%;

    --secondary: 180 70% 30%;
    --secondary-foreground: 0 0% 100%;

    --muted: 210 12% 14%;
    --muted-foreground: 210 8% 60%;

    --accent: 180 25% 16%;
    --accent-foreground: 180 50% 70%;

    --destructive: 0 63% 31%;
    --destructive-foreground: 0 0% 100%;

    --success: 160 55% 32%;
    --success-foreground: 0 0% 100%;

    --warning: 38 80% 45%;
    --warning-foreground: 0 0% 100%;

    --info: 212 55% 50%;
    --info-foreground: 0 0% 100%;

    --border: 210 12% 18%;
    --input: 210 12% 18%;
    --ring: 299 50% 58%;

    --chart-1: 299 50% 58%;
    --chart-2: 180 70% 35%;
    --chart-3: 212 55% 55%;
    --chart-4: 38 80% 55%;
    --chart-5: 160 55% 45%;

    --sidebar-background: 210 15% 7%;
    --sidebar-foreground: 210 14% 95%;
    --sidebar-primary: 299 50% 58%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 180 25% 16%;
    --sidebar-accent-foreground: 180 50% 70%;
    --sidebar-border: 210 12% 18%;
    --sidebar-ring: 299 50% 58%;
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}

import React from "react"
import type { Metadata } from "next"
import { Inter, DM_Sans, JetBrains_Mono } from "next/font/google"

import "./globals.css"

const _inter = Inter({ subsets: ["latin"], variable: "--font-inter" })
const _heading = DM_Sans({ subsets: ["latin"], weight: ["400", "500", "600", "700"], variable: "--font-heading" })
const _mono = JetBrains_Mono({ subsets: ["latin"], variable: "--font-mono" })

export const metadata: Metadata = {
  title: "NAVAX App",
  description: "Built with the NAVAX design system",
}

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode
}>) {
  return (
    <html lang="en">
      <body className={`${_inter.variable} ${_heading.variable} ${_mono.variable} font-sans antialiased`}>
        {children}
      </body>
    </html>
  )
}
